{
  "version": "1.0.0",
  "specVersion": "1.12.0",
  "name": "QCMA automated schedule",
  "description": "Automatically deployed schedule by user ID {$.getCurrentUserId}, automation ID {automationid}, run ID {runid}.",
  "enabled": true,
  "states": [
    {
      "name": "init_state",
      "type": "EVENT",
      "onEvents": [
        {
"eventRefs": [],
          "actions": [
            {
              "name": "AppReload",
              "functionRef": {
                "refName": "app.reload",
                "arguments": {
                  "appId": "{ $.loop8.item.attributes.id }",
                  "partial": false
                }
              }
            }
          ]
        }
      ]
    }
  ],
  "resourceId": "{ $.loop8.item.attributes.id }",
  "start": {
    "schedule": {
      "startDateTime": "2025-10-17T00:00:00",
      "endDateTime": null,
      "timezone": "Europe/London",
      "recurrence": "RRULE:FREQ=WEEKLY;INTERVAL=1;BYDAY=MO,TU,WE,TH,FR,SA,SU;BYHOUR={ $.reloadScheduleHour };BYMINUTE=15;BYSECOND=0"
    }
  }
}
